INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415585, 'Doxorubicin', 'Mifepristone', 'Major', 'Mifepristone may prolong the QTc interval in a dose-related manner. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided. Caution is recommended if no alternatives exist and concomitant use is required. Serum potassium should be assessed prior to starting mifepristone and 1 to 2 weeks following initiation of therapy or an increase in dosage, and periodically as needed. Hypokalemia must be corrected prior to initiation of mifepristone. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heart rhythm.', 'Synergism', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415586, 'Doxorubicin', 'Mirtazapine', 'Moderate', 'Available data suggest that mirtazapine may cause mild prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if mirtazapine is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if mirtazapine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415587, 'Doxorubicin', 'Mitomycin', 'Moderate', 'Limited data suggest that mitomycin C and doxorubicin have synergistic cardiotoxicity.', 'DDInter', 1767317476, 'Until more data are available, careful monitoring of heart function (noninvasive testing with echocardiography and/or multigated blood pool scanning and ECG) is recommended when these drugs are given together or sequentially.', 'Synergism', 'Until more data are available, careful monitoring of heart function (noninvasive testing with echocardiography and/or multigated blood pool scanning and ECG) is recommended when these drugs are given together or sequentially.', NULL, NULL, 'Moderate', 'pharmacodynamic');
