INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415620, 'Doxorubicin', 'Ozanimod', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval. Ozanimod can cause a decrease in heart rate during initiation of therapy. Coadministration of ozanimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects.', 'DDInter', 1767317476, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties. Advice from a cardiologist should be sought if treatment with ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients on concurrent therapy with QT prolonging drugs with a known risk of torsades de pointes or drugs that slow heart rate or AV conduction, or in patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents. Concomitant use of ozanimod with antineoplastic, immunosuppressive, or immune-modulating agents should be avoided according to some authorities; however, other authorities advise caution and close monitoring during coadministration and for up to 3 months after the last dose of ozanimod. When switching from drugs with prolonged immune effects to ozanimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415621, 'Doxorubicin', 'Paclitaxel (protein-bound)', 'Moderate', 'Two cases of typhlitis (neutropenic enterocolitis) have occurred in patients with metastatic breast cancer following combined treatment with paclitaxel and doxorubicin given simultaneously as 72-hour continuous intravenous infusions, while typhlitis has not been described after treatment with either drug alone. The mechanism of interaction is unknown. These drugs have been used together for the treatment of some cancers. More data are needed.', 'DDInter', 1767317476, 'Close monitoring for clinical and laboratory evidence of adverse effects is recommended during coadministration of paclitaxel and doxorubicin. In vitro data have shown that sequential administration in which paclitaxel is followed by doxorubicin may be the most effective and suitable.', 'Others', 'Close monitoring for clinical and laboratory evidence of adverse effects is recommended during coadministration of paclitaxel and doxorubicin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415622, 'Doxorubicin', 'Palifermin', 'Moderate', 'INTERVAL: In a clinical trial, administration of palifermin within 24 hours of chemotherapy resulted in increased severity and duration of oral mucositis, presumably due to increased sensitivity of the rapidly dividing epithelial cells in the immediate post-chemotherapy period.', 'DDInter', 1767317476, 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', 'Others', 'Palifermin should not be administered within 24 hours before, during infusion of, or within 24 hours after administration of myelotoxic chemotherapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
