INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415767, 'Doxorubicin', 'Voxelotor', 'Moderate', 'Coadministration with voxelotor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by voxelotor. The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', 'DDInter', 1767317476, 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voxelotor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when voxelotor is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415768, 'Doxorubicin', 'Warfarin', 'Minor', 'case report describes a 62-year-old man treated with warfarin whose prothrombin times increased by 50% to 100% during three cycles of a chemotherapeutic regimen consisting of cyclophosphamide, doxorubicin, etoposide, mechlorethamine, vincristine, procarbazine, methotrexate, and prednisone. He developed a subconjunctival hemorrhage on day 1 of the first cycle. The prothrombin time returned to normal within one week of discontinuation of warfarin. Due to the complexity of the chemotherapeutic regimen, a causal relationship to any of the medications or combination of medications cannot be established.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
