INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153565, 'Bedaquiline', 'Phenytoin', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2. Because M2 is 4- to 6-fold less active than the parent drug in terms of antimycobacterial potency, the interaction may diminish the therapeutic effect of bedaquiline.', NULL, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153728/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[4] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153566, 'Phenytoin', 'Oxybuprocaine (ophthalmic)', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', NULL, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153729/', '[1] Guay J "Methemoglobinemia related to local anesthetics: a summary of 242 episodes." Anesth Analg 108 (2009): 837-45[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Hurricaine (benzocaine topical)." Beutlich, Waukegan, IL.[4] "Product Information. Chloraseptic (benzocaine-menthol topical)." Prestige Brands (formerly MedTech), Tarrytown, NY.[5] Skold A, Cosco DL, Klein R "Methemoglobinemia: pathogenesis, diagnosis, and management." South Med J 104 (2011): 757-61[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Karim A, Ahmed S, Siddiqui R, Mattana J "Methemoglobinemia complicating topical lidocaine used during endoscopic procedures." Am J Med 111 (2001): 150-3', 1767363025);
