INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153572, 'Butabarbital', 'Phenytoin', 'Moderate', 'Barbiturates may induce or inhibit the metabolism of phenytoin. Plasma phenytoin concentrations may be decreased or increased. The mechanism are induction of hepatic CYP450 phenytoin metabolism by therapeutic doses of barbiturates, and competitive inhibition of metabolism by large doses.', NULL, 'Close laboratory and clinical observation for altered effects of both agents is recommended when doses of either agent are started, changed, or discontinued.', NULL, 'Close laboratory and clinical observation for altered effects of both agents is recommended when doses of either agent are started, changed, or discontinued. Patients should be advised to notify their physician if they experience loss of seizure control or excessive or prolonged sedative effects. It may be necessary to alter the dosage of one or both of the drugs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153735/', '[1] Kuranari M, Tatsukawa H, Seike M, et al. "Effect of phenytoin on phenobarbital pharmacokinetics in a patient with epilepsy." Ann Pharmacother 29 (1995): 83-4[2] Yoshida N, Oda Y, Nishi S, Abe J, Kaji A, Asada A, Fujimori M "Effect of barbiturate therapy on phenytoin pharmacokinetics." Crit Care Med 21 (1993): 1514-22', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153573, 'Cabotegravir', 'Phenytoin', 'Major', 'Coadministration with certain anticonvulsants or antimycobacterials may decrease the plasma concentrations of cabotegravir. The mechanism has not been clearly established but may involve induction of uridine diphosphate glucuronosyltransferase (UGT)-mediated metabolism of cabotegravir, which is primarily metabolized by UGT1A1 and to a lesser extent by UGT1A9.', NULL, 'Per the manufacturer, concomitant use of cabotegravir with carbamazepine, oxcarbazepine, phenobarbital, phenytoin, rifampin, or rifapentine is considered contraindicated.', NULL, 'Per the manufacturer, concomitant use of cabotegravir with carbamazepine, oxcarbazepine, phenobarbital, phenytoin, rifampin, or rifapentine is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153736/', '[1] "Product Information. Cabenuva (cabotegravir-rilpivirine)." ViiV Healthcare ULC, Laval, QU.[2] "Product Information. Vocabria (cabotegravir)." ViiV Healthcare, Research Triangle Park, NC.[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767363025);
