INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153610, 'Eslicarbazepine', 'Phenytoin', 'Moderate', 'Coadministration of eslicarbazepine acetate and phenytoin may decrease the systemic exposure to the active metabolite, eslicarbazepine, and increase the exposure to phenytoin. The proposed mechanisms are induction of glucuronidation by phenytoin and inhibition of CYP450 2C19 metabolism by eslicarbazepine.', NULL, 'Close observation for clinical and laboratory evidence of altered effects is recommended during concomitant use of eslicarbazepine acetate and phenytoin, particularly when one drug is started or discontinued.', NULL, 'Close observation for clinical and laboratory evidence of altered effects is recommended during concomitant use of eslicarbazepine acetate and phenytoin, particularly when one drug is started or discontinued. Patients should be advised to contact their physician if they experience loss of seizure control or signs and symptoms of phenytoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. A dosage increase for eslicarbazepine acetate and/or dosage reduction for phenytoin may be required based on efficacy and tolerability.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153773/', '[1] "Product Information. Aptiom (eslicarbazepine)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Bialer M, Soares-da-Silva P "Pharmacokinetics and drug interactions of eslicarbazepine acetate." Epilepsia 53 (2012): 935-46', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153611, 'Estazolam', 'Phenytoin', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', NULL, 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', NULL, 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153774/', '[1] Murphy A, Wilbur K "Phenytoin-diazepam interaction." Ann Pharmacother 37 (2003): 659-63[2] Sennoune S, Mesdjian E, Bonneton J, et al "Interactions between clobazam and standard antiepileptic drugs in patients with epilepsy." Ther Drug Monit 14 (1992): 269-74[3] Scott AK, Khir AS, Steele WH, et al "Oxazepam pharmacokinetics in patients with epilepsy trea