INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153644, 'Fostemsavir', 'Phenytoin', 'Major', 'Coadministration of fostemsavir with potent CYP450 3A4 inducers may significantly decrease the plasma concentrations of temsavir, the active moiety of fostemsavir. According to the prescribing information, temsavir is a substrate of CYP450 3A4, esterases, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of fostemsavir with potent CYP450 3A4 inducers is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of fostemsavir with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153807/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
