INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153716, 'Pirfenidone', 'Phenytoin', 'Moderate', 'Coadministration with potent inducers of CYP450 1A2 or other isoenzymes involved in the metabolism of pirfenidone such as CYP450 2C9 and 2C19 may significantly decrease the plasma concentrations of pirfenidone. Loss of therapeutic efficacy may result, as pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', NULL, 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', NULL, 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153880/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[3] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153717, 'Pitolisant', 'Phenytoin', 'Major', 'Coadministration with potent CYP450 3A4 inducers may decrease the systemic exposure and efficacy of pitolisant. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of pitolisant, which has been shown to be metabolized by this isoenzyme.', NULL, 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary.', NULL, 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary. For patients already receiving a stable dose of pitolisant 8.9 mg or 17.8 mg once daily, the dose of pitolisant should be increased to double the original daily dose (i.e., 17.8 mg or 35.6 mg, respectively) over 7 days. The dosage of pitolisant should be reduced by half if concomitant use of a strong CYP450 3A4 inducer is discontinued.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153881/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153718, 'Polatuzumab vedotin', 'Phenytoin', 'Minor', 'Coadministration with potent inducers of CYP450 3A4 may decrease exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. Polatuzumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153882/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Polivy (polatuzumab vedotin)." Genentech, South San Francisco, CA.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153719, 'Pomalidomide', 'Phenytoin', 'Moderate', 'Coadministration with potent inducers of CYP450 1A2, CYP450 3A4, and/or P-glycoprotein may decrease the plasma concentrations of pomalidomide, which has been shown to be primarily metabolized by these isoenzymes and also a substrate of the efflux transporter.', NULL, 'Concomitant use of pomalidomide with potent inducers of CYP450 isoenzymes and/or P-glycoprotein should generally be avoided.', NULL, 'Concomitant use of pomalidomide with potent inducers of CYP450 isoenzymes and/or P-glycoprotein should generally be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153883/', '[1] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153720, 'Ponatinib', 'Phenytoin', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of ponatinib underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ponatinib is unknown.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153884/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153721, 'Posaconazole', 'Phenytoin', 'Moderate', 'Phenytoin and other enzyme-inducing anticonvulsants may decrease the plasma concentration of posaconazole. The mechanism is induction of posaconazole clearance via the UDP-glucuronosyltransferase glucuronidation and P-glycoprotein efflux pathways.', NULL, 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk.', NULL, 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk. If coadministration is necessary, patient response to the antifungal agent must be closely monitored. If phenytoin is used, pharmacologic response and serum phenytoin levels should also be monitored closely following initiation or discontinuation of posaconazole, and the phenytoin dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153885/', '[1] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Sansone-Parsons A, Krishna G, Calzetta A, et al. "Effect of a nutritional supplement on posaconazole pharmacokinetics following oral administration to healthy volunteers." Antimicrob Agents Chemother 50 (2006): 1881-3[4] Walravens J, Brouwers J, Spriet I, Tack J, Annaert P, Augustijns P "Effect of pH and Comedication on Gastrointestinal Absorption of Posaconazole: Monitoring of Intraluminal and Plasma Drug Concentrations." Clin Pharmacokinet 50 (2011): 72