INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153787, 'Siponimod', 'Phenytoin', 'Major', 'Coadministration with drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 may decrease the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes. This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/strong CYP450 3A4 dual inducer or a moderate CYP450 2C9 inducer in combination with a separate strong CYP450 3A4 inducer.', NULL, 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.', NULL, 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.g., carbamazepine, enzalutamide, rifampin) is not recommended for all patients. Concomitant use of siponimod and moderate or strong CYP450 3A4 inducers (e.g., apalutamide, bosentan, dabrafenib, dexamethasone, efavirenz, eslicarbazepine, etravirine, fosphenytoin, lorlatinib, lumacaftor, mitotane, modafinil, nafcillin, nevirapine, phenobarbital, phenytoin, primidone, rifabutin, rifapentine, St. John''s wort) is not recommended for patients with CYP450 2C9*1/*3 and *2/*3 genotypes.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153951/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153788, 'Sirolimus', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations and pharmacologic effects of sirolimus. The mechanism probably involves reduced absorption as well as accelerated clearance of sirolimus due to induction of both intestinal P-glycoprotein drug efflux transporter and hepatic/intestinal CYP450 3A4 isoenzymes.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended. Alternative therapeutic agents with less enzyme induction potential should be considered.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153952/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363025);
