INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153812, 'Telaprevir', 'Phenytoin', 'Major', 'Coadministration with certain anticonvulsants may significantly decrease the plasma concentrations of telaprevir. The mechanism involves induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of telaprevir and other protease inhibitors. Anticonvulsants that are considered potent inducers of CYP450 3A4 include carbamazepine, phenobarbital, and phenytoin.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic telaprevir levels, concomitant use with carbamazepine, phenobarbital, or phenytoin is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic telaprevir levels, concomitant use with carbamazepine, phenobarbital, or phenytoin is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153976/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363025);
