INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153825, 'Thiotepa', 'Phenytoin', 'Moderate', 'Coadministration with phenytoin may result in significantly decreased plasma concentrations of cyclophosphamide and thiotepa, while concentrations of their main active metabolites increase. The proposed mechanism is induction of CYP450 2B6 metabolism by phenytoin.', NULL, 'Given the magnitude of the interaction, use of cyclophosphamide or thiotepa in combination with phenytoin should be avoided if possible.', NULL, 'Given the magnitude of the interaction, use of cyclophosphamide or thiotepa in combination with phenytoin should be avoided if possible. Anticonvulsants with no significant effects on CYP450 hepatic enzymes such as valproic acid, lamotrigine, or gabapentin may be appropriate alternatives. If phenytoin is required, consideration should be given to dosage reduction of the chemotherapeutic agents. In addition, plasma levels of the active metabolites should be monitored to guide further dosing.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153990/', '[1] de Jonge ME, Huitema AD, van Dam SM, Beijnen JH, Rodenhuis S "Significant induction of cyclophosphamide and thiotepa metabolism by phenytoin." Cancer Chemother Pharmacol 55 (2005): 507-10[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[5] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Kar