INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153873, 'Vorapaxar', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of vorapaxar, which is a substrate of the isoenzyme.', NULL, 'Concomitant use of vorapaxar with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, 'Concomitant use of vorapaxar with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154040/', '[1] "Product Information. Zontivity (vorapaxar)." Merck & Company Inc, Whitehouse Station, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153874, 'Voriconazole', 'Phenytoin', 'Major', 'Coadministration of phenytoin and voriconazole may significantly reduce the plasma concentrations of voriconazole and increase the plasma concentrations of phenytoin. The mechanism involves phenytoin induction of voriconazole metabolism via CYP450 2C19, 2C9 and 3A4, and voriconazole inhibition of phenytoin metabolism via CYP450 2C9 and 2C19.', NULL, 'During concomitant therapy with phenytoin, the manufacturer recommends that maintenance dosage of voriconazole be increased from 4 mg/kg to 5 mg/kg every 12 hours when given intravenously, and from 200 mg to 400 mg every 12 hours when given orally (or from 100 mg to 200 mg every 12 hours in patients who weigh less than 40 kg).', NULL, 'During concomitant therapy with phenytoin, the manufacturer recommends that maintenance dosage of voriconazole be increased from 4 mg/kg to 5 mg/kg every 12 hours when given intravenously, and from 200 mg to 400 mg every 12 hours when given orally (or from 100 mg to 200 mg every 12 hours in patients who weigh less than 40 kg). Frequent monitoring of plasma phenytoin levels and phenytoin-related adverse effects is recommended. Patients should be advised to contact their doctor if they experience symptoms of phenytoin toxicity such as nausea, vomiting, tremor, ataxia, lethargy, slurred speech, visual disturbances, and/or changes in mental status. Some experts also recommend monitoring plasma voriconazole levels, particularly in critically ill patients. Inadequate levels of voriconazole in the presence of phenytoin have been reported rarely, despite increasing voriconazole dosage as recommended. Additionally, measuring voriconazole levels following withdrawal of phenytoin can help to determine when to readjust voriconazole dosage.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154041/', '[1] Alffenaar JW, van der Elst KC, Uges DR, Kosterink JG, Daenen SM "Phenytoin-induced reduction of voriconazole serum concentration is not compensated by doubling the dosage." Br J Clin Pharmacol 68 (2009): 462-3[2] Gerzenshtein L, Patel SM, Scarsi KK, Postelnick MJ, Flaherty JP "Breakthrough Candida infections in patients receiving voriconazole." Ann Pharmacother 39 (2005): 1342-5[3] Spriet I, Meersseman P, Meersseman W, de Hoon J, Willems L "Increasing the dose of voriconazole compensates for enzyme induction by phenytoin." Br J Clin Pharmacol 69 (2010): 701-2[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] Ullmann AJ "Review of the safety, tolerability, and drug interactions of the new antifungal agents caspofungin and voriconazole." Curr Med Res Opin 19 (2003): 263-71[6] Purkins L, Wood N, Ghahramani P, Love ER, Eve MD, Fielding A "Coadministration of voriconazole and phenytoin: pharmacokinetic interaction, safety, and toleration." Br J Clin Pharmacol 56 Suppl 1 (2003): 37-44[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[8] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153875, 'Vortioxetine', 'Phenytoin', 'Moderate', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of vortioxetine, which is primarily metabolized by CYP450 2D6.', NULL, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.', NULL, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.g., carbamazepine, phenobarbital, phenytoin, rifamycins) for greater than 14 days, up to a maximum of three times the original dosage depending on clinical response. Following discontinuation of the potent CYP450 inducer, vortioxetine dosage should be returned to the original level within 14 days. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, enzalutamide, efavirenz, etravirine, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with vortioxetine is unknown.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154042/', '[1] "Product Information. Brintellix (vortioxetine)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5] "Product Information. Gluc