INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1416060, 'Enfortumab vedotin', 'Dronedarone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin. Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 1767317476, 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, hyperglycaemia, dermatologic reactions (maculopapular rash, pruritus, symmetrical drug-related intertriginous and flexural exanthema (SDRIFE), bullous dermatitis, exfoliative dermatitis, palmar-plantar erythrodysesthesia), and ocular disorders (dry eyes, keratitis, blurred vision, limbal stem cell deficiency), and the dosing of enfortumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
