INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1416251, 'Dronedarone', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of paclitaxel, which has been found in vitro to be a substrate of these isoenzymes.', 'DDInter', 1767317476, 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', 'Metabolism', 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1416252, 'Dronedarone', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors. Dosage adjustment for palbociclib is not generally considered necessary for suspected interactions with mild to moderate inhibitors. However, patients should be monitored for increased adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis.', 'Metabolism', 'Caution is advised when palbociclib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
