INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (154259, 'Sertraline', 'Pimozide', 'Major', 'Coadministration with sertraline may increase the plasma concentrations of pimozide. The mechanism of interaction has not been established. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. Moreover, sertraline itself has been associated with rare cases of QT prolongation and torsade de pointes during postmarketing use, thus an additive pharmacodynamic effect with pimozide may occur as well.', NULL, 'Given the narrow therapeutic index of pimozide and the potential for interaction with sertraline, concomitant administration of these agents is considered contraindicated.', NULL, 'Given the narrow therapeutic index of pimozide and the potential for interaction with sertraline, concomitant administration of these agents is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154426/', '[1] Krahenbuhl S, Sauter B, Kupferschmidt H, Krause M, Wyss PA, Meier PJ "Case report: reversible QT prolongation with torsades de pointes in a patient with pimozide intoxication." Am J Med Sci 309 (1995): 315-6[2] Goodnick PJ, Jerry J, Parra F "Psychotropic drugs and the ECG: focus on the QTc interval." Expert Opin Pharmacother 3 (2002): 479-98[3] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[4] "Product Information. Zoloft (sertraline)." Roerig Division, New York, NY.[5] Pacher P, Kecske