INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (154353, 'Chlorpromazine', 'Pindolol', 'Moderate', 'Some antipsychotic agents and some beta-blockers may mutually inhibit each other''s metabolism resulting in increased or additive pharmacologic effects such as hypotension, bradycardia, delirium or seizures. The mechanism may be related to competitive inhibition of CYP450 2D6-mediated first-pass metabolism.', NULL, 'Atenolol, carteolol, or nadolol may be considered as alternatives because they undergo renal elimination and are not expected to interact.', NULL, 'Atenolol, carteolol, or nadolol may be considered as alternatives because they undergo renal elimination and are not expected to interact. If this combination cannot be avoided, decreased dosage of either or both drugs may be necessary and the patient should be closely monitored for cardiovascular or nervous system adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154521/', '[1] Silver JM, Yudofsky SC, Kogan M, Katz BL "Elevation of thioridazine plasma levels by propranolol." Am J Psychiatry 143 (1986): 1290-2[2] Peet M, Middlemiss D, Yates R "Pharmacokinetic interaction between propranolol and chlorpromazine in schizophrenic patients." Lancet 2 (1980): 978[3] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[4] Miller F, Rampling D "Adverse effects of combined propranolol and chlorpromazine therapy." Am J Psychiatry 139 (1982): 1198-9[5] Vestal RE, Kornhauser DM, Hollifield JW, Shand DG "Inhibition of propranolol metabolism by chlorpromazine." Clin Pharmacol Ther 25 (1979): 19-24', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (154354, 'Clidinium', 'Pindolol', 'Moderate', 'Anticholinergic agents frequently cause drowsiness and other central nervous system-depressant effects that may be additive with those induced by beta blockers. In addition, these agents may increase heart rate and theoretically may counteract the bradycardic effects of beta blockers. Pharmacokinetically, anticholinergic agents may delay the gastrointestinal absorption of beta blockers and other drugs that are administered orally. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.g., drowsiness, dizziness, lightheadedness, confusion, blurred vision) if anticholinergic agents are used in combination with beta blockers. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154522/', '[1] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[2] Briant RH, Dorrington RE, Ferry DG, Paxton JW "Bioavailability of metoprolol in young adults and the elderly, with additional studies on the effects of metoclopramide and probanthine." Eur J Clin Pharmacol 25 (1983): 353-6[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and