INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (154439, 'Pindolol', 'Methimazole', 'Moderate', 'The clearance of some beta-blockers with high extraction ratios may be reduced when a euthyroid state is achieved after the addition of antithyroid agents. Sotalol, nadolol, carteolol, and atenolol, which are primarily eliminated by the kidney, do not appear to be affected by changes in thyroid function.', NULL, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed.', NULL, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed. Beta blocker dose reductions may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154608/', '[1] Aro A, Anttila M, Korhonen T, Sundquist H "Pharmacokinetics of propranolol and sotalol in hyperthyroidism." Eur J Clin Pharmacol 21 (1982): 373-7[2] Feely J, Stevenson IH, Crooks J "Increased clearance of propranolol in thyrotoxicosis." Ann Intern Med 94 (1981): 472-4[3] Feely J, Crooks J, Stevenson IH "The influence of age, smoking and hyperthyroidism on plasma propranolol steady state concentration." Br J Clin Pharmacol 12 (1981): 73-8[4] Feely J, Crooks J, Stevenson IH "Plasma propranolol steady state concentrations in thyroid disorders." Eur J Clin Pharmacol 19 (1981): 329-33[5] Hallengren B, Nilsson OR, Karlberg BE, Melander A, Tegler L, Wahlin-Boll E "Influence of hyperthyroidism on the kinetics of methimazole, propranolol, metoprolol and atenolol." Eur J Clin