INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1416576, 'Droperidol', 'Metronidazole', 'Minor', 'Limited data suggest that metronidazole may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with metronidazole.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1416577, 'Droperidol', 'Metyrosine', 'Moderate', 'Coadministration of metyrosine in combination with haloperidol, phenothiazines, or other dopamine antagonists may increase the risk of extrapyramidal disorders. Extrapyramidal effects have been reported during metyrosine therapy. The mechanism is inhibition of catecholamine synthesis by metyrosine. In addition, sedative effects of both drugs may be increased.', 'DDInter', 1767317476, 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
