INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1416689, 'Droperidol', 'Selegiline', 'Major', 'Anecdotal reports have implicated the concomitant use of general anesthesia and nonselective monoamine oxidase inhibitors (MAOIs) as causing hypotension or hypertension. Analysis of these cases suggests that other contributing factors may have been involved (e.g., meperidine and sympathomimetic agents).', 'DDInter', 1767317476, 'The makers of MAOIs, both selective and nonselective, consider general anesthesia to be contraindicated in patients treated with MAOIs and recommend discontinuing MAOI therapy 10 to 14 days before elective surgery.', 'Others', 'The makers of MAOIs, both selective and nonselective, consider general anesthesia to be contraindicated in patients treated with MAOIs and recommend discontinuing MAOI therapy 10 to 14 days before elective surgery.', NULL, NULL, 'Major', 'pharmacodynamic');
