INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155210, 'Bepridil', 'Pomalidomide', 'Moderate', 'Coadministration with potent inhibitors of CYP450 1A2, CYP450 3A4, and P-glycoprotein may increase the plasma concentrations of pomalidomide, which has been shown to be primarily metabolized by these isoenzymes and also a substrate of the efflux transporter. Pomalidomide exposure is increased when given with a strong CYP450 1A2 inhibitor (e.g., fluvoxamine) in the presence of a strong CYP450 3A4 and P-gp inhibitor (e.g., ketoconazole). Coadministration with ketoconazole alone did not have a clinically significant effect on exposure to pomalidomide.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.g., ciprofloxacin, fluvoxamine, tiabendazole) in the presence of strong CYP450 3A4 and P-gp inhibitors should generally be avoided. If coadministration is considered clinically necessary, the pomalidomide dose should be reduced by 50%. Dose reduction may also be required if pomalidomide is given with a strong inhibitor of CYP450 1A2 in the absence of a coadministered CYP450 3A4 and P-gp inhibitor. Patients should be monitored for occurrence of pomalidomide-related side effects, including nausea, diarrhea, and neutropenia.', 'Metabolism', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155379/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155211, 'Berotralstat', 'Pomalidomide', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Hemin, Voxelotor, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Crizanlizumab, Lanadelumab, Icatibant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155380/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155212, 'Bifidobacterium longum infantis', 'Pomalidomide', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155381/', '[1] Borriello SP, Hammes WP, Holzapfel W, et.al "Safety of probiotics that contain lactobacilli or bifidobacteria." Clin Infect Dis 36 (2003): 775-80[2] Salminen MK, Tynkkynen S, Rautelin H, et al. "Lactobacillus bacteremia during a rapid increase in probiotic use of Lactobacillus rhamnosus GG in Finland." Clin Infect Dis 35 (2002): 1155-60[3] Schlegel L, Lemerle S, Geslin P "Lactobacillus species as opportunistic pathogens in immunocompromised patients." Eur J Clin Microbiol Infect Dis 17 (1998): 887-8[4] Salminen MK, Rautelin H, Tynkkynen S,