INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155325, 'Upadacitinib', 'Pomalidomide', 'Major', 'Coadministration of upadacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and upadacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs or symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, or pain or burning during urination. If a serious or opportunistic infection develops, upadacitinib should be interrupted until the infection is controlled.', 'Others', 'Upadacitinib, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155494/', '[1] "Product Information. Rinvoq (upadacitinib)." AbbVie US LLC, North Chicago, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155326, 'Ustekinumab', 'Pomalidomide', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, More', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ustekinumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155495/', '[1] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155327, 'Valganciclovir', 'Pomalidomide', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Siponimod, Anakinra, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155496/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[5] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[6] Jung D, Griffy K, Dorr A "Effect of food on high-dose oral ganciclovir disposition in HIV-positive subjects." J Clin Pharmacol 39 (1999): 161-5[7] Lavelle J, Follansbee S, Trapnell CB, Buhles WC, Griffy KG, Jung D, Dorr A, Conner J "Effect of food on the relative bioavailability of oral ganciclovir." J Clin Pharmacol 36 (1996): 238-41', 1767369485);
