INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155329, 'Vemurafenib', 'Pomalidomide', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 and/or 2D6 isoenzymes. The mechanism is decreased clearance due to inhibition of CYP450 1A2 and 2D6 activity by vemurafenib. The interaction has been studied with caffeine and dextromethorphan, probe substrates for CYP450 1A2 and 2D6, respectively.', NULL, 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6.', NULL, 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vemurafenib is added to or withdrawn from therapy. Concomitant use of vemurafenib and substrates of CYP450 1A2 and/or 2D6 with a narrow therapeutic index (e.g., antiarrhythmics; theophylline) should be avoided if possible.', 'Metabolism', 'Tamsulosin, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155498/', '[1] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.', 1767369485);
