INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155410, 'Glycerol phenylbutyrate', 'Ponatinib', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Fedratinib, Bosutinib, Vemurafenib, Vandetanib, Cobimetinib, Ibrutinib, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155579/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155411, 'Sodium aurothiomalate', 'Ponatinib', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 'Penicillamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155580/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155412, 'Golimumab', 'Ponatinib', 'Major', 'The use of tumor necrosis factor (TNF) blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Serious infections and sepsis, including fatalities, have been reported with the use of TNF blockers, particularly in patients on concomitant immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. Concomitant use of TNF blockers with other immunosuppressants such as azathioprine or mercaptopurine may also increase the risk of a rare and often fatal cancer of white blood cells known as hepatosplenic T-Cell lymphoma (HSTCL).', NULL, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections.', NULL, 'Patients receiving a TNF blocker alone or with other immunosuppressive or myelosuppressive agents should be monitored closely for the development of infections. TNF blocker therapy should be discontinued if a serious infection or sepsis occurs. Close monitoring for signs and symptoms of HSTCL (e.g., splenomegaly, hepatomegaly, abdominal pain, persistent fever, night sweats, weight loss) is also recommended during use of TNF blockers, particularly in combination with other immunosuppressants such as azathioprine and mercaptopurine.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 'Entrectinib, Pralsetinib, Dabrafenib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Larotrectinib, Vemurafenib, Lapatinib, Selumetinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155581/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Drug labels for the Tumor Necrosis Factor-alpha (TNFa) blockers now include warnings about infection with Legionella and Listeria bacteria. Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm270849.htm." ([2011 Sept 7]):[2] "Product Information. Cimzia (certolizumab)." UCB Pharma Inc, Smyrna, GA.[3] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[4] "Product Information. Enbrel (etanercept)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[6] "Product Information. Humira (adalimumab)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155413, 'Griseofulvin', 'Ponatinib', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of ponatinib underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ponatinib is unknown.', 'Metabolism', 'Terbinafine, Salicylic acid, Selenium Sulfide, Thiabendazole, Nystatin, Ciclopirox, Terbinafine, Econazole, Flucytosine, Tioconazole, Chlorphenesin', 'Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Fedratinib, Vandetanib, Afatinib, Tivozanib, Lorlatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155582/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155414, 'Hemin', 'Ponatinib', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', NULL, 'The use of hemin with concurrent anticoagulant therapy should be avoided.', NULL, 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Betibeglogene autotemcel, Hyaluronidase, Lanadelumab, Human C1-esterase inhibitor, Crizanlizumab, Conestat alfa, Icatibant', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Larotrectinib, Bosutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155583/', '[1] "Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc, Lebanon, NJ.', 1767369485);
