    ) VALUES (1416852, 'Drospirenone', 'Metreleptin', 'Moderate', 'Treatment with metreleptin, a recombinant analog of human leptin, may affect the pharmacokinetics of drugs that are primarily metabolized by CYP450 isoenzymes.', NULL, 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin.', NULL, 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin. Clinical and laboratory monitoring are recommended following the initiation, discontinuation or change of dosage of metreleptin, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
