    ) VALUES (1416929, 'Drospirenone', 'Teriflunomide', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations of contraceptive hormones. The mechanism of interaction has not been described.', NULL, 'Caution is advised if teriflunomide is prescribed in patients receiving hormonal contraceptives due to the potential for increased adverse effects such as thromboembolism.', NULL, 'Caution is advised if teriflunomide is prescribed in patients receiving hormonal contraceptives due to the potential for increased adverse effects such as thromboembolism. Consideration should be given to the type or dose of contraceptives used in combination with teriflunomide.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
