INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155545, 'Ustekinumab', 'Ponatinib', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155714/', '[1] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155546, 'Valganciclovir', 'Ponatinib', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155715/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[5] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[6] Jung D, Griffy K, Dorr A "Effect of food on high-dose oral ganciclovir disposition in HIV-positive subjects." J Clin Pharmacol 39 (1999): 161-5[7] Lavelle J, Follansbee S, Trapnell CB, Buhles WC, Griffy KG, Jung D, Dorr A, Conner J "Effect of food on the relative bioavailability of oral ganciclovir." J Clin Pharmacol 36 (1996): 238-41', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155547, 'Varicella Zoster Vaccine (Recombinant)', 'Ponatinib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155716/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155548, 'Vemurafenib', 'Ponatinib', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of ponatinib underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ponatinib is unknown.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155717/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767363025);
