INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155656, 'Abarelix', 'Posaconazole', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155825/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155657, 'Amoxapine', 'Posaconazole', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', NULL, 'Caution and clinical monitoring are recommended.', NULL, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155826/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[3] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Ca