INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155663, 'Berotralstat', 'Posaconazole', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat. Berotralstat is a substrate of both P-gp and BCRP.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors. Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155832/', '[1] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc, Durham, NC.[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155664, 'Boceprevir', 'Posaconazole', 'Moderate', 'Coadministration of an azole antifungal agent and hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) may result in increased plasma concentrations of both drugs. The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme. Clinically, high plasma levels of an azole antifungal agent may increase the risk of QT interval prolongation and torsade de pointes arrhythmia.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors. Dosage adjustments may be required. According to the product information for boceprevir and telaprevir, high dosages of ketoconazole and itraconazole (>200 mg/day) should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155833/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363025);
