INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155685, 'Eribulin', 'Posaconazole', 'Moderate', 'Eribulin may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval. Baseline and periodic monitoring of electrocardiograms and serum electrolytes (potassium, magnesium) should be considered, and hypokalemia or hypomagnesemia corrected prior to initiating treatment with eribulin. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155854/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Halaven (eribulin)." Eisai Inc, Teaneck, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155686, 'Erlotinib', 'Posaconazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of erlotinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if erlotinib must be used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if erlotinib must be used with potent CYP450 3A4 inhibitors. A dosage reduction or temporary interruption of therapy should be considered in patients who experience undue adverse effects of erlotinib such as severe diarrhea (i.e., that which is unresponsive to loperamide or results in dehydration), severe skin reactions, or severe liver function test abnormalities.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155855/', '[1] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[2] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.[3] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[4] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155687, 'Escitalopram', 'Posaconazole', 'Major', 'Escitalopram can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if escitalopram is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if escitalopram is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. When escitalopram is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155856/', '[1] Castro VM, Clements CC, Murphy SN, et al. "QT interval and antidepressant use: a cross sectional study of electronic health records." BMJ 346 (2013): f288[2] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuti