INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155718, 'Fostamatinib', 'Posaconazole', 'Major', 'Coadministration of fostamatinib with potent inhibitors of CYP450 3A4 may significantly increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. Increased exposure to R406 may result in increased risk of adverse effects such as diarrhea, hepatotoxicity, hypertension, and neutropenia.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155887/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155719, 'Fostemsavir', 'Posaconazole', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155888/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155720, 'Galantamine', 'Posaconazole', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of galantamine, which is partially metabolized by the isoenzyme.', NULL, 'Pharmacologic response to galantamine should be monitored more closely whenever a potent CYP450 3A4 inhibitor (e.', NULL, 'Pharmacologic response to galantamine should be monitored more closely whenever a potent CYP450 3A4 inhibitor (e.g., clarithromycin, telithromycin, itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors) is added to or withdrawn from therapy, and the galantamine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience excessive cholinergic symptoms such as severe nausea, vomiting, GI cramping, salivation, lacrimation, sweating, dizziness, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155889/', '[1] "Product Information. Reminyl (galantamine)" Janssen Pharmaceuticals, Titusville, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155721, 'Gatifloxacin', 'Posaconazole', 'Major', 'Certain quinolones, including gatifloxacin and moxifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155890/', '[1] Siepmann M, Kirch W "Drug points - Tachycardia associated with moxifloxacin." Br Med J 322 (2001): 23[2] Ansari SR, Chopra N "Gatifloxacin and Prolonged QT Interval." Am J Med Sci 327 (2004): 55-6[3] Canadian Pharmacists Associatio