INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155761, 'Posaconazole', 'Phenobarbital', 'Moderate', 'Phenytoin and other enzyme-inducing anticonvulsants may decrease the plasma concentration of posaconazole. The mechanism is induction of posaconazole clearance via the UDP-glucuronosyltransferase glucuronidation and P-glycoprotein efflux pathways.', NULL, 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk.', NULL, 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk. If coadministration is necessary, patient response to the antifungal agent must be closely monitored. If phenytoin is used, pharmacologic response and serum phenytoin levels should also be monitored closely following initiation or discontinuation of posaconazole, and the phenytoin dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155930/', '[1] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Sansone-Parsons A, Krishna G, Calzetta A, et al. "Effect of a nutritional supplement on posaconazole pharmacokinetics following oral administration to healthy volunteers." Antimicrob Agents Chemother 50 (2006): 1881-3[4] Walravens J, Brouwers J, Spriet I, Tack J, Annaert P, Augustijns P "Effect of pH and Comedication on Gastrointestinal Absorption of Posaconazole: Monitoring of Intraluminal and Plasma Drug Concentrations." Clin Pharmacokinet 50 (2011)