INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155849, 'Triazolam', 'Posaconazole', 'Major', 'Coadministration with posaconazole may significantly increase the plasma concentrations and pharmacologic effects of benzodiazepines that are primarily metabolized by CYP450 3A4. The mechanism is increased bioavailability and/or decreased clearance due to inhibition of intestinal and hepatic CYP450 3A4 by posaconazole.', NULL, 'The benzodiazepine dose may require reduction during concomitant administration with posaconazole.', NULL, 'The benzodiazepine dose may require reduction during concomitant administration with posaconazole. Monitoring for increased benzodiazepine effects is recommended, and the dosage adjusted as necessary. Alternatively, benzodiazepines that are not metabolized by CYP450 3A4 (e.g., lorazepam, oxazepam, temazepam) may be considered in patients requiring posaconazole treatment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156018/', '[1] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[2] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam are increased by concomitant ingestion of grapefruit juice." Clin Pharmacol Ther 58 (1995): 127-31[3] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] "Product Information. Valium (diazepam)." Roche