INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155914, 'Idelalisib', 'Pralatrexate', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal. The risk and/or severity of diarrhea may be increased when idelalisib is used with other agents that cause diarrhea.', NULL, 'The use of idelalisib with other agents that can induce hepatotoxicity and/or diarrhea should be avoided whenever possible.', NULL, 'The use of idelalisib with other agents that can induce hepatotoxicity and/or diarrhea should be avoided whenever possible. Patients treated with idelalisib should be monitored for development of severe diarrhea or colitis and have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Permanent discontinuation of idelalisib is recommended in those who experience recurrent hepatotoxicity following dosage reduction and/or life-threatening diarrhea or intestinal perforation. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Patients should also contact their physician immediately if the number of bowel movements per day increases by six or more, or if they have new or worsening abdominal pain, chills, fever, nausea, or vomiting.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156083/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (155915, 'Indomethacin', 'Pralatrexate', 'Moderate', 'Coadministration with drugs that are subject to substantial renal clearance such as trimethoprim/sulfamethoxazole and nonsteroidal anti-inflammatory drugs (NSAIDs) may result in delayed clearance of pralatrexate. The mechanism is competitive inhibition of renal excretion, as approximately 34% of pralatrexate is eliminated unchanged by the kidney.', NULL, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs.', NULL, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs. Complete blood cell counts as well as renal and hepatic function should be closely monitored. Treatment interruption or dose reduction of pralatrexate may be required if toxicities such as mucositis, myelosuppression, or liver enzyme abnormalities develop. Patients should be advised to contact their physician if they develop oral ulcers, painful swallowing, vomiting, diarrhea, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156084/', '[1] "Product Information. Folotyn (pralatrexate)." Allos Therapeutics, Westminster, CO.', 1767363025);
