INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156287, 'Salsalate', 'Prasugrel', 'Moderate', 'Concomitant use of prasugrel with other agents that affect hemostasis including aspirin may increase the risk of bleeding. The safety of prasugrel during coadministration with other salicylates has not been established, but potentiation of gastrointestinal bleeding should be considered.', NULL, 'Although prasugrel may be used with aspirin, caution is advised during coadministration with any salicylate, especially in patients at risk for gastrointestinal ulceration or bleeding.', NULL, 'Although prasugrel may be used with aspirin, caution is advised during coadministration with any salicylate, especially in patients at risk for gastrointestinal ulceration or bleeding. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156456/', '[1] "Product Information. Effient (prasugrel)." Lilly, Eli and Company, Indianapolis, IN.', 1767363025);
