INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1417691, 'Durvalumab', 'Pomalidomide', 'Major', 'Coadministration of thalidomide or its analogs with a programmed death receptor-1 (PD-1) or programmed death ligand-1 (PD-L1) blocking antibody may increase the risk of mortality in patients with multiple myeloma. The mechanism has not been established.', 'DDInter', 1767317476, 'Concomitant use of a PD-1 or PD-L1 blocking antibody with thalidomide or a thalidomide analog plus dexamethasone in the treatment of multiple myeloma is not recommended outside of controlled clinical trials.', 'Others', 'Concomitant use of a PD-1 or PD-L1 blocking antibody with thalidomide or a thalidomide analog plus dexamethasone in the treatment of multiple myeloma is not recommended outside of controlled clinical trials.', NULL, NULL, 'Major', 'pharmacodynamic');
