INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1417828, 'Idelalisib', 'Tamsulosin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tamsulosin, which is primarily metabolized by the hepatic microsomal isoenzymes CYP450 3A4 and 2D6. Severe hypotension and priapism may occur.', 'DDInter', 1767317476, 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of tamsulosin during and for 2 weeks after treatment with itraconazole. If tamsulosin administration is discontinued for several days or more at either the 0.4 or 0.8 mg dose, therapy should be reinitiated with the 0.4 mg once-daily dose and titrated gradually as needed.', 'Metabolism', 'Since CYP450 2D6 poor metabolizers cannot be readily identified, concomitant use of tamsulosin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
