INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1417889, 'Tamsulosin', 'Perphenazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317476, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1417890, 'Tamsulosin', 'Pexidartinib', 'Moderate', 'Coadministration with pexidartinib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4 isoenzyme.', 'DDInter', 1767317476, 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when pexidartinib is prescribed in combination with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring of the CYP450 3A4 substrate drug should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
