INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1417893, 'Tamsulosin', 'Phenoxybenzamine', 'Moderate', 'Additive pharmacodynamic effects may be expected when relatively selective alpha-1A adrenoreceptor antagonists are coadministered with other alpha-blockers. Clinical data have not been reported, but there may be an increased risk and/or severity of adverse effects related to blockade of alpha-1 adrenergic receptors in the peripheral vasculature such as postural hypotension, dizziness, headache, syncope, priapism, and nasal congestion. These effects have occurred with alpha-1A adrenoreceptor antagonists even when given alone for the treatment of benign prostatic hyperplasia, despite relative selectivity for the 1A subtype of alpha-1 adrenoreceptors located in prostatic and bladder tissues.', 'DDInter', 1767317476, 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', 'Synergism', 'The use of relatively selective alpha-1A adrenoreceptor antagonists in combination with other alpha-blockers should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
