INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156518, 'Butabarbital', 'Prednisone', 'Moderate', 'Barbiturates may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by barbiturates.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156687/', '[1] Stjernholm MR, Katz FH "Effects of diphenylhydantoin, phenobarbital, and diazepam on the metabolism of methylprednisolone and its sodium succinate." J Clin Endocrinol Metab 41 (1975): 887-93[2] Sehgal VN, Srivastava G "Corticosteroid-unresponsive pemphigus vulgaris following antiepileptic therapy." Int J Dermatol 27 (1988): 258[3] Brooks SM, Werk EE, Ackerman SJ, Sullivan I, Thrasher K "Adverse effects of phenobarbital on corticosteroid metabolism in patients with bronchial asthma." N Engl J Med 286 (1972): 1125-8[4] Hancock KW, Levell MJ "Primidone/dexamethasone interaction." Lancet 2 (1978): 97-8[5] Bartoszek M, Brenner AM, Szefler SJ "Prednisolone and methylprednisolone kinetics in children receiving anticonvulsant therapy." Clin Pharmacol Ther 42 (1987): 424-32[6] Gambertoglio JG, Holford NH, Kapusnik JE, et al "Disposition of total and unbound prednisolone in renal transplant patients receiving anticonvulsants." Kidney Int 25 (1984): 119-23[7] "Product Information. Phenobarbital (phenobarbital)." Lilly, Eli and Company, Indianapolis, IN.[8] Young MC, Hughes IA "Loss of therapeutic control in congenital adrenal hyperplasia due to interaction between dexamethasone and primidone." Acta Paediatr Scand 80 (1991): 120-4[9] Brooks PM, Buchanan WW, Grove M, Downie WW "Effects of enzyme induction on metabolism of prednisolone." Ann Rheum Dis 35 (1976): 339-43[10] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156519, 'Calcium carbonate', 'Prednisone', 'Minor', 'Antacids and agents with acid-neutralizing effects may impair the absorption of dexamethasone, prednisolone, prednisone, and other corticosteroids, although data from published studies are somewhat conflicting. The mechanism of interaction and clinical significance are unknown. No particular intervention is necessary during concomitant therapy with these agents, but clinicians should be aware of the potential for interaction.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156688/', '[1] Naggar VF, Khalil SA, Gouda MW "Effect of concomitant administration of magnesium trisilicate on GI absorption of dexamethasone in humans." J Pharm Sci 67 (1978): 1029-30[2] Tanner AR, Caffin JA, Halliday JW, Powell LW "Concurrent administration of antacids and prednisone: effect on serum levels of prednisolone." Br J Clin Pharmacol 7 (1979): 397-400[3] Albin H, Vincon G, Demotes-Mainard F, et al "Effect of aluminium phosphate on the bioavailability of cimetidine and prednisolone." Eur J Clin Pharmacol 26 (1984): 271-3', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156520, 'Candida albicans', 'Prednisone', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156689/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156521, 'Carteolol', 'Prednisone', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156690/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156522, 'Frangula purshiana bark', 'Prednisone', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156691/', '[1] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8[2] Blumenthal M, Goldberg A, Brinckmann J, eds. "Herbal Medicine: Expanded Commission E Monographs." Newton, MA: Integrative Medicine Communications (2000):[3] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology