INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156747, 'Erdafitinib', 'Primidone', 'Major', 'Coadministration with potent inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', NULL, 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', NULL, 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156916/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156748, 'Ergocalciferol', 'Primidone', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156917/', '[1] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.', 1767363025);
