INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156841, 'Prednisolone', 'Primidone', 'Moderate', 'Barbiturates may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by barbiturates.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157010/', '[1] Stjernholm MR, Katz FH "Effects of diphenylhydantoin, phenobarbital, and diazepam on the metabolism of methylprednisolone and its sodium succinate." J Clin Endocrinol Metab 41 (1975): 887-93[2] Sehgal VN, Srivastava G "Corticosteroid-unresponsive pemphigus vulgaris following antiepileptic therapy." Int J Dermatol 27 (1988): 258[3] Brooks SM, Werk EE, Ackerman SJ, Sullivan I, Thrasher K "Adverse effects of phenobarbital on corticosteroid metabolism in patients with bronchial asthma." N Engl J Med 286 (1972): 1125-8[4] Hancock KW, Levell MJ "Primidone/dexamethasone interaction." Lancet 2 (1978): 97-8[5] Bartoszek M, Brenner AM, Szefler SJ "Prednisolone and methylprednisolone kinetics in children receiving ant