INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (156883, 'Sacituzumab govitecan', 'Primidone', 'Major', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may significantly decrease the plasma concentrations of sacituzumab govitecan and its pharmacologically active metabolite, SN-38. The proposed mechanism involves decreased exposure to SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1. Loss of antineoplastic efficacy may occur.', NULL, 'Due to the potential loss of antitumour activity, concomitant use of sacituzumab govitecan with UGT1A1 inducers should be avoided.', NULL, 'Due to the potential loss of antitumour activity, concomitant use of sacituzumab govitecan with UGT1A1 inducers should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157052/', '[1] "Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics, Morris Plains, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363025);
