INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1418017, 'Flibanserin', 'Duvelisib', 'Major', 'Coadministration with moderate and potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of flibanserin, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', 'DDInter', 1767317476, 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated. When initiating flibanserin following treatment with a moderate or potent CYP450 3A4 inhibitor, the manufacturer recommends waiting until 2 weeks after the last dose of CYP450 3A4 inhibitor. Conversely, if a moderate or potent CYP450 3A4 inhibitor is required during flibanserin therapy, flibanserin should be discontinued at least 2 days before starting the CYP450 3A4 inhibitor.', 'Metabolism', 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
