INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157057, 'Ranitidine (bismuth citrate)', 'Procainamide', 'Moderate', 'A study in healthy volunteers suggests that ranitidine may increase the plasma concentrations of procainamide and its active metabolite, N-acetylprocainamide (NAPA), in a dose-dependent manner. The proposed mechanism is competitive inhibition of renal tubular secretion by ranitidine.', NULL, 'Until more information is available, caution is advised when prescribing procainamide with high dosages of ranitidine such as those used in the treatment of Zollinger-Ellison syndrome.', NULL, 'Until more information is available, caution is advised when prescribing procainamide with high dosages of ranitidine such as those used in the treatment of Zollinger-Ellison syndrome. In addition, caution may be advisable in certain patient populations such as the elderly or those with liver disease who may experience elevated ranitidine levels at lower dosages. If an interaction is suspected, a dosage adjustment for procainamide or a switch to famotidine or nizatidine may be considered.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157226/', '[1] Somogyi A, Bochner F "Dose and concentration dependent effect of ranitidine on procainamide disposition and renal clearance in man." Br J Clin Pharmacol 18 (1984): 175-81[2] Rodvold KA, Paloucek FP, Jung D, Gallastegui J "Interaction of steady-state procainamide with H2-receptor antagonists cimetidine and ranitidine." Ther Drug Monit 9 (1987): 378-83[3] Martin BK "Effect of ranitidine on procainamide disposition." Br J Clin Pharmacol 19 (1985): 858-60[4] Rocci ML, Kosoglou T, Ferguson RK, Vlas