INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157255, 'Siponimod', 'Procarbazine', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects.', NULL, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod. When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157424/', '[1] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157256, 'Sipuleucel-T', 'Procarbazine', 'Moderate', 'Neither the leukapheresis procedure nor sipuleucel-T have been studied for use in combination with chemotherapy or immunosuppressive agents (e.g., systemic corticosteroids). Since sipuleucel-T is designed to stimulate the immune system, concurrent use of myelo- or immunosuppressive agents may alter the efficacy and/or safety of sipuleucel-T.', NULL, 'Patients should be carefully evaluated to determine whether it is medically appropriate to reduce or discontinue chemotherapy or immunosuppressive agents prior to treatment with sipuleucel-T.', NULL, 'Patients should be carefully evaluated to determine whether it is medically appropriate to reduce or discontinue chemotherapy or immunosuppressive agents prior to treatment with sipuleucel-T.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157425/', '[1] "Product Information. Provenge (sipuleucel-T)." Dendreon Corporation, Seattle, WA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157257, 'Smallpox (Vaccinia) Vaccine, Live', 'Procarbazine', 'Major', 'The administration of live smallpox virus vaccine during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued for at least 3 months in most cases. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157426/', '[1] "Product Information. Dryvax (smallpox vaccine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] CDC. Centers for Disease Control and Prevention "Smallpox vaccination clinic guide. Logistical considerations and guidance for state and local planning for emergency, large-scale, voluntary administration of smallpox vaccine in response to a smallpox outbreak. Available from: URL: http://www.bt.cdc.gov" ([2002 Oct 16]):', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157258, 'Sodium phosphate, monobasic (p32)', 'Procarbazine', 'Moderate', 'The concomitant use of bone marrow depressants and sodium phosphate P-32 may have additive myelosuppressive effects.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with sodium phosphate P-32.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with sodium phosphate P-32. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157427/', '[1] AnazaoHealth Corporation "P32 Sodium Phosphate. Available from: URL: http://dailymed.nlm.nih.gov/dailymed/fda/fdaDrugXsl.cfm?setid=735f92e3-cc92-4d9b-afe8-f935a685ee78" ([2012 Jun]):', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157259, 'Solriamfetol', 'Procarbazine', 'Major', 'Coadministration of solriamfetol with other drugs that also inhibit the reuptake of dopamine or norepinephrine, including monoamine oxidase inhibitors (MAOIs), may increase the risk of hypertensive reactions including death, stroke, myocardial infarction, aortic dissection, pulmonary edema, and renal failure. Additionally, MAOIs can increase dopaminergic and noradrenergic activity by inhibiting the metabolism of dopamine and norepinephrine.', NULL, 'Concomitant use of solriamfetol with MAOIs is considered contraindicated.', NULL, 'Concomitant use of solriamfetol with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with solriamfetol.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157428/', '[1] "Product Information. Sunosi (solriamfetol)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Zyvox (linezolid)" Pharmacia and Upjohn, Kalamazoo, MI.[4] Zetin M, Plon L, DeAntonio M "MAOI reaction with powdered protein dietary supplement." J Clin Psychiatry 48 (1987): 499[5] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[6] Pohl R, Balon R, Berchou R "Reaction to chicken nuggets in a patient taking an MAOI." Am J Psychiatry 145 (1988): 651[7] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[8] Tailor SA, Shulman KI, Walker SE, Moss J, Gardner D "Hypertensive episode associated with phenelzine and tap beer--a re