INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157304, 'Valbenazine', 'Procarbazine', 'Major', 'Coadministration of valbenazine with monoamine oxidase inhibitors (MAOIs) may increase the risk of adverse effects such as serotonin syndrome or reduce the therapeutic effects of valbenazine. The mechanism for this interaction has not been delineated; however, concomitant use of these drugs may increase the concentration of monoamine neurotransmitters in synapses.', NULL, 'The use of valbenazine with MAOIs should generally be avoided.', NULL, 'The use of valbenazine with MAOIs should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Alternative treatment may be required if an interaction is suspected.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157473/', '[1] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] "Product Information. Zyvox (linezolid)" Pharmacia and Upjohn, Kalamazoo, MI.[4] Zetin M, Plon L, DeAntonio M "MAOI reaction with powdered protein dietary supplement." J Clin Psychiatry 48 (1987): 499[5] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[6] Pohl R, Balon R, Berchou R "Reaction to chicken nuggets in a patient taking an MAOI." Am J Psychiatry 145 (1988): 651[7] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[8] Tailor SA, Shulman KI, Walker SE, Moss J, Gardner D "Hypertensive episode associated with phenelzine and tap beer--a reanalysis of the role of pressor amines in beer." J Cl