    ) VALUES (1418364, 'Minocycline', 'Methoxyflurane', 'Major', 'Coadministration of tetracycline or oxytetracycline with methoxyflurane has enhanced renal toxicity. The mechanism is not known.', NULL, 'This combination should be avoided if possible.', NULL, 'This combination should be avoided if possible.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
