    ) VALUES (1418398, 'Iron sucrose', 'Minocycline', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of tetracyclines. The proposed mechanism is chelation of tetracyclines to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide. Omadacycline administration with sucroferric oxyhydroxide should be separated by 4 hours. No dosing recommendations are available for use with other tetracyclines, although patients should consider following the same precaution.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
