        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1418414, 'Zinc acetate', 'Minocycline', 'Moderate', 'INTERVAL: The bioavailability of oral tetracyclines may be significantly decreased during concurrent administration with zinc-containing products. Therapeutic failure may result. The proposed mechanism is chelation of tetracyclines by the zinc cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours.', NULL, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours. Doxycycline may be an appropriate alternative, since its absorption may not be significantly affected by zinc coadministration.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
