    ) VALUES (1418447, 'Isradipine', 'Encorafenib', 'Moderate', 'Coadministration with encorafenib may alter the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, resulting in increased toxicity or decreased efficacy of these drugs.', NULL, 'Concomitant use of encorafenib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of encorafenib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
